-- -----------------------------------------------------
-- View `haslpms4`.`v_user`
-- -----------------------------------------------------
DROP VIEW IF EXISTS `haslpms4`.`v_user` ;
DROP TABLE IF EXISTS `haslpms4`.`v_user`;
USE `haslpms4`;
CREATE  OR REPLACE VIEW `v_user` AS
select a.*, b.name as company_name from user a
left join company b on a.company_id = b.id;

-- -----------------------------------------------------
-- View `haslpms4`.`v_project_funds`
-- -----------------------------------------------------
DROP VIEW IF EXISTS `haslpms4`.`v_project_funds` ;
DROP TABLE IF EXISTS `haslpms4`.`v_project_funds`;
USE `haslpms4`;
CREATE  OR REPLACE VIEW `v_project_funds` AS
select a.*, b.name as project_name from project_funds a
left join project b on a.project_id = b.id;

-- -----------------------------------------------------
-- View `haslpms4`.`v_project_eval`
-- -----------------------------------------------------
DROP VIEW IF EXISTS `haslpms4`.`v_project_eval` ;
DROP TABLE IF EXISTS `haslpms4`.`v_project_eval`;
USE `haslpms4`;
CREATE  OR REPLACE VIEW `v_project_eval` AS
select a.*, b.name as project_name, c.name as company_name, c.type as company_type from project_eval a
left join project b on a.project_id = b.id
left join company c on a.company_id = c.id;

-- -----------------------------------------------------
-- View `haslpms4`.`v_article`
-- -----------------------------------------------------
DROP VIEW IF EXISTS `haslpms4`.`v_article` ;
DROP TABLE IF EXISTS `haslpms4`.`v_article`;
USE `haslpms4`;
CREATE  OR REPLACE VIEW `v_article` AS
select a.*, b.name as type_name, c.name as type_1_name, c.id as type_1_id from article a
left join article_type b on a.type_id = b.id
left join article_type c on b.parent_id = c.id;

-- -----------------------------------------------------
-- View `haslpms4`.`v_project`
-- -----------------------------------------------------
DROP VIEW IF EXISTS `haslpms4`.`v_project` ;
DROP TABLE IF EXISTS `haslpms4`.`v_project`;
USE `haslpms4`;
CREATE  OR REPLACE VIEW `v_project` AS
select a.*, (a.zjly_zhong+a.zjly_sheng+a.zjly_shi+a.zjly_xian) tzgm, b.name as type_name, c.name as type_name_1, c.id as type_1 from project a
left join project_type b on a.type_id = b.id
left join project_type c on b.parent_id = c.id;

-- -----------------------------------------------------
-- View `haslpms4`.`v_workflow`
-- -----------------------------------------------------
DROP VIEW IF EXISTS `haslpms4`.`v_workflow` ;
DROP TABLE IF EXISTS `haslpms4`.`v_workflow`;
USE `haslpms4`;
CREATE  OR REPLACE VIEW `v_workflow` AS
select a.*, b.name as project_name, c.name as type_name, 
	d.name as user_name, e.name as current_user_name from workflow a
left join project b on a.project_id = b.id
left join workflow_type c on a.type_id = c.id
left join user d on a.user_id = d.id
left join user e on a.current_user_id = e.id;

