-- -----------------------------------------------------
-- View `haslpms4`.`v_user_project`
-- -----------------------------------------------------
DROP VIEW IF EXISTS `haslpms4`.`v_user_project` ;
DROP TABLE IF EXISTS `haslpms4`.`v_user_project`;
USE `haslpms4`;
CREATE  OR REPLACE VIEW `v_user_project` AS
select a.user_id as user_id, b.* from project_user a
left join v_project b on a.project_id = b.id
union
select a.user_id as user_id, b.* from project_user a
right join v_project b on a.project_id = b.id;

