DROP TABLE IF EXISTS `haslpms4`.`v_project_company`;
USE `haslpms4`;
CREATE  OR REPLACE VIEW `v_project_company` AS
select a.*,b.name as project_name, c.name as company_name, c.legal_person as company_legal_person, c.mobile as company_mobile, c.email as company_email from project_company a
left join project b on a.project_id = b.id
left join company c on a.company_id = c.id;

-- -----------------------------------------------------
-- View `haslpms4`.`v_project_user`
-- -----------------------------------------------------
DROP VIEW IF EXISTS `haslpms4`.`v_project_user` ;
DROP TABLE IF EXISTS `haslpms4`.`v_project_user`;
USE `haslpms4`;
CREATE  OR REPLACE VIEW `v_project_user` AS
select a.*, 
	b.name as project_name, 
    c.name as user_name, c.company_id, c.email as user_email, c.mobile as user_mobile,
    c.job as user_job, c.title as user_title,
    d.name as company_name from project_user a
left join project b on a.project_id = b.id
left join user c on a.user_id = c.id
left join company d on c.company_id = d.id;

